sys_ws_operation.operation_script:
var body = {};
var params = request.body.data;
//===============================
var article_type = "text";
var kb_category = "8deb2552ff0131009b20fffffffffffc"; //IT
var kb_knowledge_base = "a7e8a78bff0221009b20ffffffffff17"; //FAQ
var short_description = params.short_description;
var text = params.text;
//================================
var gr = new GlideRecord('kb_knowledge');
var gd = new GlideDate();
gr.article_type = article_type;
gr.kb_category = kb_category;
gr.kb_knowledge_base = kb_knowledge_base;
gr.short_description = short_description;
gr.text = text;
gr.published = gd;
gr.workflow_state = "published";
gr.insert();
//================================
var kb_sys_id = gr.sys_id;
var kb_number = gr.number;

//Create Task

if(kb_sys_id){
	var gt = new GlideRecord('sysapproval_approver');
	gt.approval_column = "approval";
	gt.approval_journal_column = "approval_history";
	gt.approver = "6816f79cc0a8016401c5a33be04be441";
	gt.document_id = kb_sys_id;
	gt.source_table = "kb_knowledge";
	gt.approval_source = "ui.desktop";
	gt.state = 'approved';
	gt.insert();

	//This update is needed after approval
	//if not the status will remain on 'draft'
	gr.workflow_state = "published";
	gr.updateMultiple();

	body['approval_sys_id'] = gt.sys_id;
	body['approval_status'] = gt.state;
}


//================================
body['Number'] = kb_number;
body['kb_sys_id'] = kb_sys_id;
response.setBody(body);
