var params = request.body.data;
//===============================
var requestedFor = params.u_requested_for;
var requestedForEmail = params.u_requested_for_email;
var requestType = params.u_request_type;
var whatNeeded = params.u_what_needed;
var whenNeeded = params.u_when_needed;
//================================
var gr = new GlideRecord('x_58872_needit_needit');
var gd = new GlideDate();
gr.u_requested_for = requestedFor;
gr.u_requested_for_email = requestedForEmail;
gr.u_request_type = requestType;
gr.u_what_needed = whatNeeded;
gr.u_when_needed = whenNeeded;
